import React from "react";
import { useState } from "react";

import "./Project.css";

import Card from "../Card/Card";

const Project = (props) => {
  console.log(props);
  const [state, setState] = useState({
    hover: false,
    colorStyle: "color-1",
  });

  const flipCard = () => {
    setState({ ...state, hover: !state.hover });
  };

  return (
    <div
      className="Project col-12 col-sm-4 col-lg-3 my-3 p-2"
      onClick={flipCard}
    >
      <Card name={state.hover ? "flip-on" : "flip-off"} />
    </div>
  );
};

export default Project;
