import React, { useEffect } from "react";

import "./Profile.css";

import { motion, useAnimation } from "framer";

import { useInView } from "react-intersection-observer";

import BackgroundWorker from "../BackgroundWorker/BackgroundWorker";
import FlechaDown from "../FlechaDown/FlechaDown";

import fotoPerfil from "../../images/fotoPerfil.png";
import PersonalLogo from "../PersonalLogo/PersonalLogo";
import CodeLanguajes from "../CodeLanguajes/CodeLanguajes";
import LogoNoMove from "../LogoNoMove/LogoNoMove";

const Profile = () => {
  const controls = useAnimation();
  const [ref, inView] = useInView({ threshold: 0.5 });

  useEffect(() => {
    if (inView) {
      controls.start((i) => ({
        opacity: 1,
        transition: { duration: 4, delay: i * 0.7 },
      }));
    }
  }, [controls, inView]);

  return (
    <div className="Profile bg-light">
      <BackgroundWorker />
      <FlechaDown />
      <div className="container-fluid">
        <div className="row justify-content-center p-2">
          <div className="col-12 col-sm-6 col-lg-5">
            <div className="row justify-content-center ">
              <motion.div
                ref={ref}
                className="col-12"
                initial={{ opacity: 0 }}
                animate={controls}
                transition={{ delay: 2, duration: 2 }}
              >
                <div className="col-12 col-sm-6 ">
                  <img
                    className="foto img-fluid rounded"
                    src={fotoPerfil}
                    alt="Foto de perfil"
                  />
                </div>
                <h3 className="col-12 text-center mt-1">Sobre mí</h3>
                <p className="col-12 text-justify">
                  Full Stack Developer en &lt;<code>construción</code>&gt;,
                  diplomado en trabajo social, apasionado de las nuevas
                  tecnologías y de las ciencias sociales.
                </p>
                <p className="col-12 text-justify">
                  Hace un año decidi hacer una cambio laboral que en su momento
                  parecia un giro de 180º, pasar de ser trabajador social a web
                  developer.
                </p>
                <p className="col-12 text-justify">
                  Y digo que que parecia de 180º porque no pueden existir mas
                  similitudes entre estas dos profesiones.... Trabajas con
                  personas y para personas, la escucha, asertividad, habilidades
                  sociales y el trabajo en equipo es son fundamentales.
                </p>
                <p className="col-12 text-justify">
                  Tras este año solo puedo decir que vuelvo a sentir en casa y
                  parte de ese sentimiento he querido que quedase reflejado en
                  mi logo....
                </p>
              </motion.div>
            </div>
            <div className="col-12 col-sm-6 col-lg-5 justify-content-center">
              <PersonalLogo />
            </div>
          </div>
          <div className="col-12 col-sm-6 col-lg-5 justify-content-center">
            {/* <CodeLanguajes ref={ref} controls={controls} /> */}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
