import React from "react";
import { useState } from "react";

import "./Projects.css";

import Card from "../Card/Card";

const Project = () => {
  const [state, setState] = useState({
    hover: false,
    colorStyle: "color-1",
  });

  const hoverOn = () => {
    setState({ ...state, hover: true });
  };

  const hoverOff = () => {
    setState({ ...state, hover: false });
  };

  return (
    <div className="Profile d-flex flex-row justify-content-center align-items-center">
      <div
        className={this.state.colorStyle}
        onMouseEnter={hoverOn}
        onMouseLeave={hoverOff}
      >
        <Card name={state.hover ? "hover-on" : "hover-off"} />
      </div>
    </div>
  );
};

export default Project;
