import React, { useEffect } from "react";

import "./Header.css";

import { useInView } from "react-intersection-observer";
import Logo from "../Logo/Logo";
import Background from "../Background/Background";
// import FlechaDown from "../FlechaDown/FlechaDown";

const Header = () => {
  return (
    <div className="Header container-fluid">
      <Background />
      <Logo />
      <div className="d-flex flex-column flex-sm-row justify-content-center mt-4">
        <button
          type="button"
          class="d-none d-lg-block btn btn-dark mx-3 btn-lg"
        >
          Sobre Mi
        </button>
        <button
          type="button"
          class="d-none btn btn-dark my-3 my-sm-0 mx-3 btn-lg"
        >
          Mis proyectos
        </button>
        <button type="button" class="d-none btn btn-dark mx-3 btn-lg">
          Hablemos
        </button>
      </div>
    </div>
  );
};

export default Header;
