import React, { useEffect } from "react";

import { useInView } from "react-intersection-observer";
import { motion, useAnimation } from "framer";

const Box = () => {
  const controls = useAnimation();
  const [ref, inView] = useInView();

  useEffect(() => {
    if (inView) {
      controls.start("visible");
    }
  }, [controls, inView]);

  return (
    <div>
      <motion.div
        ref={ref}
        animate={controls}
        initial="hidden"
        style={{
          padding: 40,
          background: "blue",
          display: "inline-block",
          marginTop: 500,
          marginLeft: 40,
          marginBottom: 500,
          borderRadius: 5,
        }}
        variants={{
          visible: { opacity: 1, x: 0, duration: 7 },
          hidden: { opacity: 0, x: 300 },
        }}
      />
    </div>
  );
};

export default Box;
