import React from "react";
import { useState } from "react";

import "./Project.css";

import Card from "../Card/Card";

const Project = () => {
  const [state, setState] = useState({
    hover: false,
  });

  const flipCard = () => {
    setState({ hover: !state.hover });
  };

  return (
    <div className="col-12 col-sm-6 col-lg-4 p-4 m-auto">
      <div className="Project col-12" onClick={flipCard}>
        <Card name={state.hover ? "flip-on" : "flip-off"} />
      </div>
    </div>
  );
};

export default Project;
