import React, { useEffect } from "react";

import { motion, useAnimation } from "framer";

import { useInView } from "react-intersection-observer";

const Header = () => {
  const controls = useAnimation();
  const [ref, inView] = useInView();

  useEffect(() => {
    if (inView) {
      controls.start("visible");
    }
  }, [controls, inView]);

  controls.start((index) => ({
    opacity: 1,
    display: "block",
    x: "0vw",
    transition: { duration: 5, delay: index * 5 },
  }));

  return (
    <div className="Header">
      <motion.h1
        custom={0}
        style={{ width: "90vw", fontFamily: "Roboto" }}
        initial={{ x: "120vw", display: "none", opacity: 0 }}
        animate={controls}
        whileTap={{ scale: 0.8 }}
      >
        Hello
      </motion.h1>
      <motion.h1
        custom={1}
        style={{ width: "90vw", fontFamily: "Roboto" }}
        initial={{ x: "120vw", display: "none", opacity: 0 }}
        animate={controls}
      >
        My name is Manuel
      </motion.h1>
      <motion.h1
        custom={2}
        style={{ width: "90vw", fontFamily: "Roboto" }}
        initial={{ x: "120vw", display: "none", opacity: 0 }}
        animate={controls}
      >
        Web Developer
      </motion.h1>
    </div>
  );
};

export default Header;
