import React from "react";

import { motion } from "framer";
import { Frame } from "framer";

const Header = () => {
  const onDrag = (event, info) => {
    console.log(info.point.x, info.point.y);
  };

  return (
    <div className="Header">
      <motion.h1
        style={{ width: "90vw", fontFamily: "Roboto" }}
        initial={{ x: "120vw", display: "none" }}
        animate={{ x: "0vw", display: "block" }}
        transition={{ duration: 5 }}
        whileTap={{ scale: 0.8 }}
      >
        Hello
      </motion.h1>
      <motion.h1
        style={{ width: "90vw", fontFamily: "Roboto" }}
        initial={{ x: "120vw", display: "none" }}
        animate={{ x: "0vw", display: "block" }}
        transition={{ duration: 5 }}
      >
        My name is Manuel
      </motion.h1>
      <motion.h1
        style={{ width: "90vw", fontFamily: "Roboto" }}
        initial={{ x: "120vw", display: "none" }}
        animate={{ x: "0vw", display: "block" }}
        transition={{ duration: 5 }}
      >
        Web Developer
      </motion.h1>
      <Frame drag onDrag={onDrag}></Frame>
    </div>
  );
};

export default Header;
