import React from "react";
import "./Header.css";

import { useEffect } from "react";

import { useInView } from "react-intersection-observer"; // react-intersection-observer

import { motion, useAnimation } from "framer-motion"; // Framer Motion

function Header() {
  const [ref, inView] = useInView({
    rootMargin: "-100px 0px",
  });
  const controls = useAnimation();

  useEffect(() => {
    if (inView) {
      controls.start((i) => ({
        display: "flex",
        opacity: 1,
        x: 0,
        // transition: { delay: i * 3, duration: 5 },
      }));
    }
  }, [inView, controls]);

  return (
    <div className="Pepe">
      <motion.div
        style={{
          margin: 0,
          width: "50vw",
          border: "1px solid black",
        }}
        ref={ref}
        custom={0}
        initial={{ opacity: 0, x: "25vw", display: "none" }}
        animate={controls}
      >
        <h1>Hola Soy Manu Fernandez Freire</h1>
      </motion.div>
      {/* <motion.div
        style={{
          margin: 0,
          width: "50vw",
          border: "1px solid black",
        }}
        ref={ref}
        custom={1}
        initial={{ opacity: 0, x: "25vw", display: "none" }}
        animate={controls}
      >
        <h1>Web developer</h1>
      </motion.div>
      <motion.div
        style={{
          margin: 0,
          width: "50vw",
          border: "1px solid black",
        }}
        ref={ref}
        custom={2}
        initial={{ opacity: 0, x: "25vw", display: "none" }}
        animate={controls}
      >
        <h1>Bienvenido a mi portfolio</h1>
      </motion.div> */}
    </div>
  );
}

export default Header;
