import React, { useEffect } from "react";

import "./Profile.css";

import { motion, useAnimation } from "framer";

import { useInView } from "react-intersection-observer";

import BackgroundWorker from "../BackgroundWorker/BackgroundWorker";
import FlechaDown from "../FlechaDown/FlechaDown";

import fotoPerfil from "../../images/fotoPerfil.png";
import CodeLanguajes from "../CodeLanguajes/CodeLanguajes";

const Profile = () => {
  const controls = useAnimation();
  const [ref, inView] = useInView({ threshold: 0.5 });

  useEffect(() => {
    if (inView) {
      controls.start((i) => ({
        opacity: 1,
        transition: { duration: 4, delay: i * 0.7 },
      }));
    }
  }, [controls, inView]);

  return (
    <div className="Profile bg-light">
      <BackgroundWorker />
      <FlechaDown />
      <div className="container-fluid">
        <div className="row justify-content-center p-2">
          <div className="col-12 col-sm-6 col-lg-5">
            <div className="row justify-content-center ">
              <motion.div
                ref={ref}
                className="col-12"
                initial={{ opacity: 0 }}
                animate={controls}
                transition={{ delay: 2, duration: 2 }}
              >
                <div className="col-12 offset-sm-3 col-sm-6 ">
                  <img
                    className="foto img-fluid rounded"
                    src={fotoPerfil}
                    alt="Foto de perfil"
                  />
                </div>
                <h3 className="col-12 text-center">Sobre mí</h3>
                <p className="col-12 text-justify">
                  Full Stack Developer en &lt;<code>construción</code>&gt;,
                  diplomado en trabajo social, apasionado de las nuevas
                  tecnologías y de las ciencias sociales.
                </p>
                <p className="col-12 text-justify">
                  Hace un año decidi hacer una cambio laboral que en su momento
                  parecia un giro de 180º, pasar de ser trabajador social a web
                  developer.
                </p>
                <p>
                  Mirando hacia atras lo que más me ha asombrado es la cantidad
                  de similitudes que existen entre estas dos profesiones, en las
                  que trabajas con personas y para personas, en los que la
                  escucha y el trabajo en equipo es fundamental. Me vuelvo a
                  sentir en casa!! Mi logo habla de todo esto....
                </p>
                <p className="col-10 text-justify">
                  Creo en ambas profesiones y soy un firme defensor de la
                  alfabetización digital de la sociedad y las posibilidades que
                  brinda en el cambio social.
                </p>
              </motion.div>
            </div>
          </div>
          <div className="col-12 col-sm-6 col-lg-5 justify-content-center border">
            <CodeLanguajes ref={ref} controls={controls} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
