import React, { useEffect } from "react";

import "./Header.css";

import { motion, useAnimation } from "framer";

import { useInView } from "react-intersection-observer";

const Header = () => {
  const controls = useAnimation();
  const [ref, inView] = useInView();

  useEffect(() => {
    if (inView) {
      controls.start("visible");
    }
  }, [controls, inView]);

  useEffect(() => {
    console.log(inView);
    if (inView) {
      controls.start((index) => ({
        opacity: 1,
        display: "flex",
        x: "0vw",
        transition: { duration: 5, delay: index * 5 },
      }));
    }
  }, [controls, inView]);

  return (
    <div className="Header">
      <motion.h1
        ref={ref}
        custom={0}
        className="heading"
        initial={{ x: "120vw", opacity: 0 }}
        animate={controls}
        whileTap={{ scale: 0.8 }}
      >
        Hello
      </motion.h1>
      <motion.h1
        ref={ref}
        custom={1}
        className="heading"
        initial={{ x: "120vw", opacity: 0 }}
        animate={controls}
      >
        My name is Manuel
      </motion.h1>
      <motion.h1
        ref={ref}
        custom={2}
        className="heading"
        initial={{ x: "120vw", opacity: 0 }}
        animate={controls}
      >
        Web Developer
      </motion.h1>
    </div>
  );
};

export default Header;
