import React, { useEffect } from "react";

import "./Profile.css";

import { motion, useAnimation } from "framer";

import { useInView } from "react-intersection-observer";

import BackgroundWorker from "../BackgroundWorker/BackgroundWorker";
import FlechaDown from "../FlechaDown/FlechaDown";

import fotoPerfil from "../../images/fotoPerfil.png";

const Profile = () => {
  const controls = useAnimation();
  const [ref, inView] = useInView({ threshold: 0.5 });

  useEffect(() => {
    console.log(inView);
    if (inView) {
      controls.start((i) => ({
        opacity: 1,
      }));
    }
  }, [controls, inView]);

  return (
    <div
      ref={ref}
      className="Profile d-flex flex-column align-items-center bg-light justify-content-center"
    >
      <motion.div
        className="foto col-12 col-md-5 mt-4 mt-md-0"
        initial={{ opacity: 0 }}
        animate={{ controls }}
        transition={{ duration: 3 }}
      >
        hola
        {/* <div className="col-12 col-md-4 m-auto">
          <img
            className="img-fluid rounded"
            src={fotoPerfil}
            alt="Foto de perfil"
          />
        </div>
        <h3 className="mt-2">Sobre mí</h3>
        <p className="text-center px-1">
          Full Stack Developer en construcción, apasionado de las nuevas
          tecnologías y de las ciencias sociales, por eso soy diplomado en
          trabajo social.....
          <br /> Soy un firme defensor de la alfabetización digital de la
          sociedad y las posibilidades que brinda en el cambio social.
        </p> */}
      </motion.div>
      <div className=""></div>
    </div>
  );
};

export default Profile;
