import React from "react";
import { useEffect } from "react";

import { motion, useAnimation } from "framer";
import { useInView } from "react-intersection-observer";

import "./CodeLanguajes.css";

import reactJS from "../../images/reactJS.png";
import html5 from "../../images/html5.png";
import css from "../../images/css.png";
import js from "../../images/js.png";
import mongo from "../../images/mongo.png";
import github from "../../images/github.png";
import angular from "../../images/angular.png";
import nodeJS from "../../images/nodeJS.png";
import express from "../../images/express.png";

const CodeLanguajes = () => {
  const controls = useAnimation();
  const [ref, inView] = useInView({ threshold: 0.5 });

  useEffect(() => {
    console.log(inView, "Background");
    if (inView) {
      controls.start((i) => ({
        opacity: 1,
        scale: 1,
      }));
    }
  }, [controls, inView]);

  return (
    <div ref={ref} className="codelanguajes">
      <h4 className="mb-2">Lenguajes de programación</h4>
      <div className="d-flex flex-row justify-content-center flex-wrap p-2 mb-2">
        <div className="col-4 col-md-2 m-1">
          <motion.img
            className="codelanguajeslogo img-fluid"
            src={reactJS}
            alt="reactJs"
            initial={{ opacity: 0, scale: 0.1 }}
            animate={controls}
            transition={{
              delay: 4,
              duration: 5,
            }}
          />
        </div>
        <div className="col-4 col-md-2 m-1">
          <motion.img
            className="codelanguajeslogo img-fluid"
            src={angular}
            alt="reactJs"
            initial={{ opacity: 0 }}
            animate={controls}
            transition={{
              delay: 5,
              duration: 5,
            }}
          />
        </div>
        <div className="col-4 col-md-2 m-1 ">
          <motion.img
            className="codelanguajeslogo img-fluid"
            src={js}
            alt="reactJs"
            initial={{ opacity: 0 }}
            animate={controls}
            transition={{
              delay: 6,
              duration: 5,
            }}
          />
        </div>
        <div className="col-4 col-md-2 m-1 ">
          <motion.img
            className="codelanguajeslogo img-fluid"
            src={html5}
            alt="reactJs"
            initial={{ opacity: 0 }}
            animate={controls}
            transition={{
              delay: 7,
              duration: 5,
            }}
          />
        </div>
        <div className="col-4 col-md-2 m-1">
          <motion.img
            className="codelanguajeslogo img-fluid"
            src={css}
            alt="reactJs"
            initial={{ opacity: 0 }}
            animate={controls}
            transition={{
              delay: 8,
              duration: 5,
            }}
          />
        </div>
        <div className="col-4 col-md-2 m-1">
          <motion.img
            className="codelanguajeslogo img-fluid"
            src={mongo}
            alt="reactJs"
            initial={{ opacity: 0 }}
            animate={controls}
            transition={{
              delay: 9,
              duration: 5,
            }}
          />
        </div>
        <div className="col-4 col-md-2 m-1">
          <motion.img
            className="codelanguajeslogo img-fluid"
            src={github}
            alt="reactJs"
            initial={{ opacity: 0 }}
            animate={controls}
            transition={{
              delay: 10,
              duration: 5,
            }}
          />
        </div>
        <div className="col-4 col-md-2 m-1">
          <motion.img
            className="codelanguajeslogo img-fluid"
            src={nodeJS}
            alt="reactJs"
            initial={{ opacity: 0 }}
            animate={controls}
            transition={{
              delay: 11,
              duration: 5,
            }}
          />
        </div>
        <div className="col-4 col-md-2 m-1">
          <motion.img
            className="codelanguajeslogo img-fluid"
            src={express}
            alt="reactJs"
            initial={{ opacity: 0 }}
            animate={controls}
            transition={{
              delay: 12,
              duration: 5,
            }}
          />
        </div>
      </div>
    </div>
  );
};

export default CodeLanguajes;
