import React from "react";
import Axios from "axios";
import { Formik, yup } from "formik";

import useForm from "../../hooks/useForm";

import Background from "../Background/Background";
import LogoContact from "../LogoContact/LogoContact";

import linkedin from "../../images/Contact/Linkedin.png";
import github from "../../images/backgroundCode/github.png";
import twitter from "../../images/Contact/Twitter.png";
import SocialContactLogo from "../SocialContactLogo/SocialContactLogo";

const socialContact = [
  {
    name: "linkedin",
    image: linkedin,
    source: "https://www.linkedin.com/in/manufernandezfreire/",
  },
  {
    name: "Git Hub",
    image: github,
    source: "https://github.com/mfernandezfreire",
  },
  {
    name: "Twitter",
    image: twitter,
    source: "https://twitter.com/manuferfreire",
  },
];

const schema = yup.object({
  email: yup.string().required(),
});

const Contact = () => {
  const [state, handleState, resetState] = useForm({
    email: "",
    subject: "",
    message: "",
  });

  const handleSubmit = (e) => {
    const email = state.email;
    const subject = state.subject;
    const message = state.message;
    e.preventDefault();
    Axios.post(`http://localhost:4000/send-email`, { email, subject, message })
      .then((_) => {
        resetState();
        console.log("everything it's ok");
      })
      .catch((error) => console.log("Nothing it's ok"));
    resetState();
  };

  return (
    <div className="Contact container-fluid">
      <Background type={"contact"} />
      <h1 className="display-4 text-center">Contacta Conmigo</h1>
      <div className="row justify-content-center">
        <div className="col-6 mb-5">
          <LogoContact />
        </div>
      </div>
      <Formik
        validationSchema={schema}
        onSubmit={console.log}
        initialValues={{ email: "introduce aquÃ­ tu correo" }}
      ></Formik>
      <form onSubmit={handleSubmit}>
        <div className="row justify-content-center mb-5">
          <div className="col-9 col-sm-3 mr-2">
            <div className="form-group">
              <label for="emailDeContacto" className="h5 mb-3">
                Email de Contacto
              </label>
              <input
                type="email"
                className="form-control border-0"
                style={{ background: "gainsboro", color: "black" }}
                id="emailDeContacto"
                placeholder="name@ejemplo.com"
                name="email"
                value={state.email}
                onChange={handleState}
              />
            </div>
            {/* <div className="form-group">
              <label for="asunto" className="h5 mb-3">
                Asunto
              </label>
              <input
                type="text"
                class="form-control border-0"
                style={{ background: "gainsboro", color: "black" }}
                id="asunto"
                placeholder="Asunto"
                name="subject"
                value={state.subject}
                onChange={handleState}
              />
            </div>
          </div>
          <div className="col-9 col-sm-3 ml-2">
            <div className="form-group">
              <label for="Mensaje" className="h5 mb-3">
                Mensaje
              </label>
              <textarea
                className="form-control border-0"
                style={{ background: "gainsboro", color: "black" }}
                rows="5"
                id="Mensaje"
                name="message"
                value={state.message}
                onChange={handleState}
              ></textarea>
            </div> */}
          </div>
          <div className="col-12 text-center">
            <button type="submit" className="btn btn-secondary btn-lg">
              Enviar
            </button>
          </div>
        </div>
      </form>
      <div className="row justify-content-center">
        {socialContact.map((social) => (
          <SocialContactLogo
            name={social.name}
            image={social.image}
            source={social.source}
          />
        ))}
      </div>
      <div className="row mt-5">
        <div className="col-12">
          <p className="text-center mb-0" style={{ fontSize: "1.5rem" }}>
            Manuel Fernandez Freire Â©
          </p>
          <p className="text-center" style={{ fontSize: "1.5rem" }}>
            2020
          </p>
        </div>
      </div>
    </div>
  );
};

export default Contact;
