import React from "react";

import { motion, useAnimation } from "framer";

const Header = () => {
  const controls = useAnimation();

  controls.start((index) => ({
    opacity: 1,
    display: "block",
    transition: { delay: index * 0.5 },
  }));

  return (
    <div className="Header">
      <motion.h1
        custom={0}
        style={{ width: "90vw", fontFamily: "Roboto" }}
        initial={{ x: "120vw", display: "none", opacity: 0 }}
        animate={controls}
        transition={{ duration: 5 }}
        whileTap={{ scale: 0.8 }}
      >
        Hello
      </motion.h1>
      {/* <motion.h1
        style={{ width: "90vw", fontFamily: "Roboto" }}
        initial={{ x: "120vw", display: "none", opacity: 0 }}
        animate={{ x: "0vw", display: "block" }}
        transition={{ duration: 5 }}
      >
        My name is Manuel
      </motion.h1>
      <motion.h1
        style={{ width: "90vw", fontFamily: "Roboto" }}
        initial={{ x: "120vw", display: "none", opacity: 0 }}
        animate={{ x: "0vw", display: "block" }}
        transition={{ duration: 5 }}
      >
        Web Developer
      </motion.h1> */}
    </div>
  );
};

export default Header;
