import React from "react";

import "./Profile.css";
import BackgroundWorker from "../BackgroundWorker/BackgroundWorker";
import FlechaDown from "../FlechaDown/FlechaDown";

import fotoPerfil from "../../images/fotoPerfil.png";

const Profile = () => {
  return (
    <div className="Profile d-flex flex-column align-items-center bg-light justify-content-center">
      <div className="foto col-12 col-md-5 mt-4 mt-md-0">
        <img
          className="col-md-4 img-rounded mb-2"
          src={fotoPerfil}
          alt="Foto de perfil"
        />
        <h3>Sobre mí</h3>
        <p className="text-justify px-1">
          Full Stack Developer en construcción, apasionado de las nuevas
          tecnologías y de las ciencias sociales. Soy un firme defensor de la
          alfabetización digital de la sociedad y las posibilidades que brinda
          en el cambio social. Javascript | MongoDB | Express | React | Node.js
          | ES6 | HTML | CSS
        </p>
      </div>
    </div>
  );
};

export default Profile;
