import React from "react";
import "./Header.css";

import { useInView } from "react-intersection-observer";
import { motion, useAnimation } from "framer-motion";
import { useEffect } from "react";

import "bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

function Header() {
  const [ref, inView] = useInView({
    rootMargin: "-100px 0px",
  });
  const controls = useAnimation();

  useEffect(() => {
    if (inView) {
      controls.start((i) => ({
        opacity: 1,
        x: 0,
        transition: { delay: i * 3, duration: 5 },
      }));
    }
  }, [inView, controls]);

  return (
    <div className="pepe d-flex flex-column align-items-center justify-content-center">
      <motion.div
        ref={ref}
        custom={3}
        initial={{ opacity: 0, x: 500 }}
        animate={controls}
      >
        <h1>Este es mi logo</h1>
      </motion.div>
      <motion.div
        ref={ref}
        custom={0}
        initial={{ opacity: 0, x: 500 }}
        animate={controls}
      >
        <h1>Hola Soy Manu Fernandez Freire</h1>
      </motion.div>
      <motion.div
        ref={ref}
        custom={1}
        initial={{ opacity: 0, x: -500 }}
        animate={controls}
      >
        <h1>Web developer</h1>
      </motion.div>
      <motion.div
        ref={ref}
        custom={2}
        initial={{ opacity: 0, x: 500 }}
        animate={controls}
      >
        <h1>Bienvenido a mi portfolio</h1>
      </motion.div>
    </div>
  );
}

export default Header;
