import React, { useEffect } from "react";

import "./Profile.css";

import { motion, useAnimation } from "framer";

import { useInView } from "react-intersection-observer";

import BackgroundWorker from "../BackgroundWorker/BackgroundWorker";
import FlechaDown from "../FlechaDown/FlechaDown";

import fotoPerfil from "../../images/fotoPerfil.png";
import CodeLanguajes from "../CodeLanguajes/CodeLanguajes";

const Profile = () => {
  const controls = useAnimation();
  const [ref, inView] = useInView({ threshold: 0.5 });

  useEffect(() => {
    if (inView) {
      controls.start((i) => ({
        opacity: 1,
        transition: { duration: 4, delay: i * 0.7 },
      }));
    }
  }, [controls, inView]);

  return (
    <div className="Profile bg-light">
      <BackgroundWorker />
      <FlechaDown />
      <div className="container-fluid">
        <div className="row justify-content-center">
          <div className="col-12 col-sm-6 col-lg-5">
            <div className="row justify-content-center">
              <motion.div
                ref={ref}
                className="col-12 foto"
                initial={{ opacity: 0 }}
                animate={controls}
                transition={{ delay: 2, duration: 2 }}
              >
                <img
                  className="col-6 img-fluid rounded"
                  src={fotoPerfil}
                  alt="Foto de perfil"
                />
                <h3 className="col-12 text-center">Sobre mí</h3>
                <p className="col-12 text-justify">
                  Full Stack Developer en &lt;<code>construción</code>&gt;,
                  diplomado en trabajo social, apasionado de las nuevas
                  tecnologías y de las ciencias sociales.
                </p>
                <p className="col-12 text-justify">
                  Soy un firme defensor de la alfabetización digital de la
                  sociedad y las posibilidades que brinda en el cambio social.
                </p>
              </motion.div>
            </div>
          </div>
          <div className="col-12 col-sm-6 col-lg-5 justify-content-center">
            <CodeLanguajes ref={ref} controls={controls} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
