import React, { useEffect } from "react";

import "./Header.css";

import { motion, useAnimation } from "framer";

import { useInView } from "react-intersection-observer";

const Header = () => {
  const controls = useAnimation();
  const [ref, inView] = useInView();

  useEffect(() => {
    if (inView) {
      controls.start((i) => ({
        opacity: 1,
        x: "0vw",
        transition: { duration: 3, delay: i * 2 },
      }));
    }
  }, [controls, inView]);

  return (
    <div
      className="Header d-flex flex-column align-items-center bg-success"
      ref={ref}
    >
      <motion.h1
        custom={0}
        className="heading display-4 m-4 p-1"
        initial={{ x: "120vw", opacity: 0 }}
        animate={controls}
        whileTap={{ scale: 0.8 }}
      >
        Hello
      </motion.h1>
      <motion.h1
        custom={1}
        className="heading display-4 m-4 p-1"
        initial={{ x: "120vw", opacity: 0 }}
        animate={controls}
      >
        My name is Manuel
      </motion.h1>
      <motion.h1
        custom={2}
        className="heading display-4 m-4 p-1"
        initial={{ x: "120vw", opacity: 0 }}
        animate={controls}
      >
        Web Developer
      </motion.h1>
    </div>
  );
};

export default Header;
