import React from "react";
import "./Header.css";

import { useEffect } from "react";

import { useInView } from "react-intersection-observer"; // react-intersection-observer

import { motion, useAnimation } from "framer-motion"; // Framer Motion

function Header() {
  const [ref, inView] = useInView({
    rootMargin: "-100px 0px",
  });
  const controls = useAnimation();

  useEffect(() => {
    if (inView) {
      controls.start((i) => ({
        opacity: 1,
        x: 0,
        transition: { delay: i * 3, duration: 5 },
      }));
    }
  }, [inView, controls]);

  return (
    <div className="Header">
      <motion.div
        style={{
          width: "50vw",
          border: "1px solid black",
          overflow: "hidden",
          position: "relative",
        }}
        ref={ref}
        custom={0}
        initial={{ opacity: 0, x: "60vw" }}
        animate={controls}
      >
        <h1>Hola Soy Manu Fernandez Freire</h1>
      </motion.div>
      <motion.div
        style={{
          width: "50vw",
          border: "1px solid black",
          overflow: "hidden",
          position: "relative",
        }}
        ref={ref}
        custom={1}
        initial={{ opacity: 0, x: "0vw" }}
        animate={controls}
      >
        <h1>Web developer</h1>
      </motion.div>
      <motion.div
        style={{
          width: "50vw",
          border: "1px solid black",
        }}
        ref={ref}
        custom={2}
        initial={{ opacity: 0, x: "60vw" }}
        animate={controls}
      >
        <h1>Bienvenido a mi portfolio</h1>
      </motion.div>
    </div>
  );
}

export default Header;
