import React from "react";

import SocialContactLogo from "../SocialContactLogo/SocialContactLogo";

import linkedin from "../../images/Contact/Linkedin.png";
import github from "../../images/backgroundCode/github.png";
import twitter from "../../images/Contact/Twitter.png";

const socialContact = [
  {
    name: "linkedin",
    image: linkedin,
    source: "https://www.linkedin.com/in/manufernandezfreire/",
  },
  {
    name: "Git Hub",
    image: github,
    source: "https://github.com/mfernandezfreire",
  },
  {
    name: "Twitter",
    image: twitter,
    source: "https://twitter.com/manuferfreire",
  },
];

const Footer = () => {
  return (
    <div
      className="Footer row mt-5 mb-0 bg-dark p-0"
      style={{ minHeight: "200px", zIndex: 1, opacity: 1 }}
    >
      <div className="d-flex flex-column align-items-center">
        <div className="row justify-content-center my-4">
          {socialContact.map((social) => (
            <SocialContactLogo
              name={social.name}
              image={social.image}
              source={social.source}
            />
          ))}
        </div>
        <p className="text-center mt-4 mb-0">Manuel Fernandez Freire Â©</p>
        <p className="text-center mt-0">2020</p>
      </div>
    </div>
  );
};

export default Footer;
