import React from "react";
import "./Header.css";

import { useEffect } from "react";

import { useInView } from "react-intersection-observer"; // react-intersection-observer

import { motion, useAnimation } from "framer-motion"; // Framer Motion

function Header() {
  const [ref, inView] = useInView({
    rootMargin: "-100px 0px",
  });
  const controls = useAnimation();

  useEffect(() => {
    if (inView) {
      controls.start((i) => ({
        opacity: 1,
        x: 0,
        position: "relative",
        overflow: "hidden",
        transition: { delay: i * 3, duration: 5 },
      }));
    }
  }, [inView, controls]);

  return (
    <div className="Header">
      <motion.div
        style={{
          width: "90vw",
          border: "1px solid black",
          position: "relative",
          overflow: "hidden",
        }}
        ref={ref}
        custom={0}
        initial={{
          opacity: 0,
          x: "100vw",
          position: "relative",
          overflow: "hidden",
        }}
        animate={controls}
      >
        <h1>Este es mi logo</h1>
