import React from "react";

import manoSpock from "../../images/engranaje.png";

import { motion } from "framer";

import "./Background.css";

import reactJS from "../../images/reactJS.png";
import html5 from "../../images/html5.png";
import css from "../../images/css.png";
import js from "../../images/js.png";
import mongo from "../../images/mongo.png";
import github from "../../images/github.png";
import git from "../../images/git.png";
import angular from "../..//images/angular.png";

const Background = () => {
  const background = [reactJS, html5, css, js, mongo, github, git, angular];
  return (
    <div className="Background d-flex flex-row">
      <motion.img
        src={background[Math.floor(Math.random() * 8)]}
        alt="Mano Spock"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 3 }}
        style={{
          position: "absolute",
          top: "5vw",
          left: "6vw",
          width: "30vw",
          opacity: 0.5,
        }}
      />
      <motion.img
        src={background[Math.floor(Math.random() * 8)]}
        alt="Mano Spock"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 3 }}
        style={{
          position: "absolute",
          top: "1vw",
          left: "30vw",
          width: "30vw",
          opacity: 0.5,
        }}
      />
      <motion.img
        src={background[Math.floor(Math.random() * 8)]}
        alt="Mano Spock"
        // animate={{ rotate: 360 }}
        // transition={{ loop: Infinity, ease: "linear", duration: 40 }}
        style={{
          position: "absolute",
          bottom: "5vw",
          left: "8vw",
          width: "18vw",
          opacity: 0.5,
        }}
      />
      <motion.img
        src={background[Math.floor(Math.random() * 8)]}
        alt="Mano Spock"
        style={{
          position: "absolute",
          bottom: "2vw",
          right: "30vw",
          width: "18vw",
          opacity: 0.5,
        }}
      />
      <motion.img
        src={background[Math.floor(Math.random() * 8)]}
        alt="Mano Spock"
        style={{
          position: "absolute",
          top: "3vw",
          right: "5vw",
          width: "16vw",
          opacity: 0.5,
        }}
      />
      <motion.img
        src={background[Math.floor(Math.random() * 8)]}
        alt="Mano Spock"
        style={{
          position: "absolute",
          top: "3vw",
          right: "30vw",
          width: "16vw",
          opacity: 0.5,
        }}
      />
      <motion.img
        src={background[Math.floor(Math.random() * 8)]}
        alt="Mano Spock"
        style={{
          position: "absolute",
          top: "15vw",
          right: "15vw",
          width: "16vw",
          opacity: 0.5,
        }}
      />
      <motion.img
        src={background[Math.floor(Math.random() * 8)]}
        alt="Mano Spock"
        // animate={{ rotateY: 360 }}
        // transition={{ loop: Infinity, ease: "linear", duration: 40 }}
        style={{
          position: "absolute",
          bottom: "7vw",
          right: "8vw",
          width: "18vw",
          opacity: 0.5,
        }}
      />
      <motion.img
        src={background[Math.floor(Math.random() * 8)]}
        alt="Mano Spock"
        style={{
          position: "absolute",
          bottom: "7vw",
          left: "30vw",
          width: "18vw",
          opacity: 0.5,
        }}
      />
    </div>
  );
};

export default Background;
