import React from "react";
import "./App.css";

import { motion, useAnimation } from "framer-motion";
import { useEffect } from "react";

import "bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

function App() {
  const controls = useAnimation();

  useEffect(() => {
    controls.start((i) => ({
      opacity: 1,
      x: 0,
      transition: { delay: i * 3, duration: 5 },
    }));
  }, []);

  return (
    <div className="pepe d-flex flex-column align-items-center justify-content-center">
      <motion.div
        custom={0}
        initial={{ opacity: 0, x: 500 }}
        animate={controls}
      >
        <h1>Hola Soy Manu Fernandez Freire</h1>
      </motion.div>
      <motion.div
        custom={1}
        initial={{ opacity: 0, x: -500 }}
        animate={controls}
      >
        <h1>Web developer</h1>
      </motion.div>
      <motion.div
        custom={2}
        initial={{ opacity: 0, x: 500 }}
        animate={controls}
      >
        <h1>Bienvenido a mi portfolio</h1>
      </motion.div>

      {/* <ul>
         <motion.li custom={0} initial={{ x: 300 }} animate={controls}>
           <h1>hello</h1>
         </motion.li>
         <motion.li custom={1} animate={controls}>
           <h1>hello</h1>
         </motion.li>
         <motion.li custom={2} animate={controls}>
           <h1>hello</h1>
         </motion.li>
       </ul> */}
    </div>
  );
}

export default App;
