import React from "react";

import manoSpock from "../../images/engranaje.png";

import { motion } from "framer";

import "./Background.css";

const Background = () => {
  return (
    <motion.div className="Background">
      <img
        src={manoSpock}
        alt="Mano Spock"
        initial={{ x: "40vw", opacity: 0 }}
        animate={controls}
        transition={{ duration: 1 }}
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
        style={{
          position: "absolute",
          top: "5vw",
          left: "9vw",
          width: "15vw",
          opacity: 0.5,
        }}
      />
    </motion.div>
  );
};

export default Background;
