import React from "react";
import "./Projects.css";
import screenshot from "../../images/Captura_de_pantalla_2020-03-25_a_las_13.56.21_iizx0y.png";

import { useInView } from "react-intersection-observer";
import { motion, useAnimation } from "framer-motion";
import { useEffect } from "react";

import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";

function Projects() {
  const [ref, inView] = useInView({
    rootMargin: "-100px 0px",
  });

  const controls = useAnimation();

  useEffect(() => {
    if (inView) {
      controls.start((i) => ({
        display: "flex",
        opacity: 1,
        x: 0,
        transition: { delay: i * 3, duration: 5 },
      }));
    }
  }, [inView, controls]);

  return (
    <div className="pepe d-flex flex-column align-items-center justify-content-center">
      <motion.div
        ref={ref}
        custom={0}
        initial={{ opacity: 0, x: 400, display: "none" }}
        animate={controls}
      >
        <Card style={{ width: "60vw", heigth: "30vh" }}>
          <Card.Img variant="top" src={screenshot} />
          <Card.Body>
            <Card.Title>Card Title 1</Card.Title>
            <Card.Text>
              Some quick example text to build on the card title and make up the
              bulk of the card's content.
            </Card.Text>
            <Button variant="primary">Go somewhere</Button>
            <Button variant="primary">Go somewhere</Button>
          </Card.Body>
        </Card>
      </motion.div>
      <motion.div
        ref={ref}
        custom={0}
        initial={{ opacity: 0, x: "-15vw" }}
        animate={controls}
      >
        <Card style={{ width: "60vw", heigth: "30vh" }}>
          <Card.Img variant="top" src={screenshot} />
          <Card.Body>
            <Card.Title>Card Title 2</Card.Title>
            <Card.Text>
              Some quick example text to build on the card title and make up the
              bulk of the card's content.
            </Card.Text>
            <Button variant="primary">Go somewhere</Button>
            <Button variant="primary">Go somewhere</Button>
          </Card.Body>
        </Card>
      </motion.div>
      <motion.div
        ref={ref}
        custom={0}
        initial={{ opacity: 0, x: "15vw" }}
        animate={controls}
      >
        <Card style={{ width: "60vw", heigth: "30vh" }}>
          <Card.Img variant="top" src={screenshot} />
          <Card.Body>
            <Card.Title>Card Title 3</Card.Title>
            <Card.Text>
              Some quick example text to build on the card title and make up the
              bulk of the card's content.
            </Card.Text>
            <Button variant="primary">Go somewhere</Button>
            <Button variant="primary">Go somewhere</Button>
          </Card.Body>
        </Card>
      </motion.div>
    </div>
  );
}

export default Projects;
