import React, { useEffect } from "react";

import { useInView } from "react-intersection-observer";
import { motion, useAnimation } from "framer";

import "./FlechaDown.css";

import flechaDown from "../../images/Paging/flechaDown.png";

const FlechaDown = () => {
  const controls = useAnimation();
  const [ref, inView] = useInView({ threshold: 0.5 });
  useEffect(() => {
    if (inView) {
      controls.start((i) => ({
        opacity: 1,
        transition: { duration: 3, delay: i * 0.7 },
      }));
    }
  }, [controls, inView]);

  return (
    <div className="FlechaDown row justify-content-center align-items-end">
      <div className="col-2 col-sm-1">
        <div className="row">
          <motion.img
            src={flechaDown}
            alt="Logo de desarrollo web"
            className="img-fluid"
            initial={{ opacity: 0, scale: 0.2 }}
            animate={{
              opacity: 1,
              scale: 0.6,
            }}
            transition={{
              delay: 4,
              duration: 3,
              yoyo: Infinity,
            }}
          />
        </div>
      </div>
    </div>
  );
};

export default FlechaDown;
