import React, { useEffect } from "react";

import "./Profile.css";

import { motion, useAnimation } from "framer";

import { useInView } from "react-intersection-observer";

import BackgroundWorker from "../BackgroundWorker/BackgroundWorker";
import FlechaDown from "../FlechaDown/FlechaDown";

import fotoPerfil from "../../images/fotoPerfil.png";
import PersonalLogo from "../PersonalLogo/PersonalLogo";
import CodeLanguajes from "../CodeLanguajes/CodeLanguajes";

const Profile = () => {
  const controls = useAnimation();
  const [ref, inView] = useInView({ threshold: 0.5 });

  useEffect(() => {
    if (inView) {
      controls.start((i) => ({
        opacity: 1,
        transition: { duration: 4, delay: i * 0.7 },
      }));
    }
  }, [controls, inView]);

  return (
    <div className="Profile bg-light">
      <BackgroundWorker />
      <FlechaDown />
      <div className="container-fluid">
        <div
          className="row justify-content-center p-4
        "
        >
          <div className="col-12 col-sm-6 col-lg-6">
            <div className="row justify-content-center ">
              <motion.div
                ref={ref}
                className="col-12"
                initial={{ opacity: 0 }}
                animate={controls}
                transition={{ delay: 2, duration: 2 }}
              >
                <div className="row">
                  <div className="col-12 col-sm-4">
                    <img
                      className="foto img-fluid rounded"
                      src={fotoPerfil}
                      alt="Foto de perfil"
                    />
                  </div>
                  <div className="col-12 col-sm-8">
                    <div className="row justify-content-center">
                      <h3 className="col-12 text-center">Sobre mí</h3>
                      <p className="col-12 text-justify">
                        Full Stack Developer en &lt;<code>construción</code>
                        &gt;, diplomado en trabajo social, apasionado de las
                        nuevas tecnologías y de las ciencias sociales.
                      </p>
                      <p className="col-12 text-justify">
                        Hace un año decidi hacer una cambio laboral que en su
                        momento parecia un giro de 180º, pasar de ser trabajador
                        social a web developer. Y solo lo parecia porque no
                        pueden existir mas similitudes entre estas dos
                        profesiones.... Trabajas con personas y para personas,
                        la escucha, asertividad, habilidades sociales y el
                        trabajo en equipo es son fundamentales.
                      </p>
                      <p className="col-12 text-justify">
                        Tras este año solo puedo decir que vuelvo a sentir en
                        casa y parte de ese sentimiento he querido que quedase
                        reflejado en mi logo....
                      </p>
                    </div>
                  </div>
                </div>
              </motion.div>
            </div>
          </div>
          <div className="col-12 col-sm-6 col-lg-6 border">
            <PersonalLogo />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
