import React from "react";

import { motion } from "framer";

import "./Projects.css";

const Project = () => {
  const variants = {
    hidden: { rotateY: 180, opacity: 0, transition: { duration: 3 } },
    visible: { opacity: 1 },
  };

  const variants1 = {
    whileHover: !initial,
    visible: { rotateY: 180, opacity: 1, transition: { duration: 3 } },
    hidden: { opacity: 0 },
  };

  return (
    <div className="Project d-flex flex-column align-items-center justify-content-center">
      <div>
        <motion.div
          className="cardFlip bg-success"
          style={{ width: "15vw", height: "30vh" }}
          initial="visible"
          whileHover="hidden"
          variants={variants}
        >
          <motion.div></motion.div>
        </motion.div>
      </div>
    </div>
  );
};

export default Project;
