import React from "react";
import { useEffect } from "react";

import { motion, useAnimation } from "framer";
import { useInView } from "react-intersection-observer";

import "./CodeLanguajes.css";

import reactJS from "../../images/reactJS.png";
import html5 from "../../images/html5.png";
import css from "../../images/css.png";
import js from "../../images/js.png";
import mongo from "../../images/mongo.png";
import github from "../../images/github.png";
import angular from "../../images/angular.png";
import nodeJS from "../../images/nodeJS.png";
import express from "../../images/express.png";

const CodeLanguajes = (props) => {
  return (
    <div ref={props.ref} className="codelanguajes">
      <motion.h4
        custom={2}
        className="mb-2"
        initial={{ opacity: 0 }}
        animate={props.controls}
        transition={{ duration: 1 }}
      >
        Lenguajes de programaci√≥n
      </motion.h4>
      <div className="d-flex flex-row justify-content-center flex-wrap p-2 mb-2">
        <div className="col-4 col-md-2 m-1">
          <motion.img
            custom={5.5}
            className="codelanguajeslogo img-fluid"
            src={reactJS}
            alt="reactJs"
            initial={{ opacity: 0 }}
            animate={props.controls}
            transition={{ duration: 1 }}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95, rotate: 90 }}
          />
        </div>
        <div className="col-4 col-md-2 m-1">
          <motion.img
            custom={6}
            className="codelanguajeslogo img-fluid"
            src={angular}
            alt="reactJs"
            initial={{ opacity: 0 }}
            animate={props.controls}
            transition={{ duration: 1 }}
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.95, rotate: -90 }}
          />
        </div>
        <div className="col-4 col-md-2 m-1 ">
          <motion.img
            custom={5.6}
            className="codelanguajeslogo img-fluid"
            src={js}
            alt="reactJs"
            initial={{ opacity: 0 }}
            animate={props.controls}
            transition={{ duration: 1 }}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95, rotate: 90 }}
          />
        </div>
        <div className="col-4 col-md-2 m-1 ">
          <motion.img
            custom={5.8}
            className="codelanguajeslogo img-fluid"
            src={html5}
            alt="reactJs"
            initial={{ opacity: 0 }}
            animate={props.controls}
            transition={{ duration: 1 }}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95, rotate: -90 }}
          />
        </div>
        <div className="col-4 col-md-2 m-1">
          <motion.img
            custom={5}
            className="codelanguajeslogo img-fluid"
            src={css}
            alt="reactJs"
            initial={{ opacity: 0 }}
            animate={props.controls}
            transition={{ duration: 1 }}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95, rotate: 90 }}
          />
        </div>
        <div className="col-4 col-md-2 m-1">
          <motion.img
            custom={6.2}
            className="codelanguajeslogo img-fluid"
            src={mongo}
            alt="reactJs"
            initial={{ opacity: 0 }}
            animate={props.controls}
            transition={{ duration: 1 }}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95, rotate: -90 }}
          />
        </div>
        <div className="col-4 col-md-2 m-1">
          <motion.img
            custom={6.4}
            className="codelanguajeslogo img-fluid"
            src={github}
            alt="reactJs"
            initial={{ opacity: 0 }}
            animate={props.controls}
            transition={{ duration: 1 }}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95, rotate: 90 }}
          />
        </div>
        <div className="col-4 col-md-2 m-1">
          <motion.img
            custom={6.6}
            className="codelanguajeslogo img-fluid"
            src={nodeJS}
            alt="reactJs"
            initial={{ opacity: 0 }}
            animate={props.controls}
            transition={{ duration: 1 }}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95, rotate: 90 }}
          />
        </div>
        <div className="col-4 col-md-2 m-1">
          <motion.img
            custom={6.8}
            className="codelanguajeslogo img-fluid"
            src={express}
            alt="reactJs"
            initial={{ opacity: 0 }}
            animate={props.controls}
            transition={{ duration: 1 }}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95, rotate: 90 }}
          />
        </div>
      </div>
    </div>
  );
};

export default CodeLanguajes;
